export interface IActionObject {
    name: string;
    url: string;
    open_in_new_tab?: boolean;
}
export interface IAvatarObject {
    action_url?: string;
    avatar_url: string;
}
export interface ISubTextObject {
    action_url?: string;
    text: string;
}
export interface IRemoteNotificationMessage {
    header?: string;
    schema: string;
    text: string;
    url?: string;
    open_in_new_tab?: boolean;
    extra_data?: string;
    actions?: IActionObject[];
    avatar?: IAvatarObject;
    subtext?: ISubTextObject;
}
export interface IRemoteNotification {
    n_id: string;
    n_category: string;
    created_on: number;
    seen_on?: number;
    interacted_on?: number;
    tags?: string[];
    expiry?: number;
    is_expiry_visible: boolean;
    is_pinned: boolean;
    can_user_unpin?: boolean;
    archived?: boolean;
    message: IRemoteNotificationMessage;
}
export interface IConfigStore {
    collectorApiUrl: string;
    apiUrl: string;
    socketUrl: string;
    pageSize: number;
}
export interface INotificationStore {
    unseenCount: number;
    stores: {
        [store_id: string]: {
            notifications: IRemoteNotification[];
            unseenCount: number;
            isFirstCall: boolean;
        };
    };
    activeStoreId: string | null;
    hasNext: boolean;
    pageNumber: number;
    initialLoading: boolean;
    fetchMoreLoading: boolean;
    initialFetchTime: number | null;
}
export interface IStore {
    storeId: string;
    label: string;
    query?: {
        tags?: string | string[];
        categories?: string | string[];
        read?: boolean;
        archived?: boolean;
    };
}
export interface IStateStore {
    notifications: IRemoteNotification[];
    unseenCount: number;
    isFirstCall: boolean;
}
